	function createSortFunc(attr, dir, comp, store){
		return function(itemA, itemB){
			return store.getValue ?
				//Old store
				dir * comp(store.getValue(itemA, attr), store.getValue(itemB, attr)) :
				//New store
				dir * comp(itemA[attr], itemB[attr]);
		};
	}

